import React, { useContext } from 'react';
import { placeOrder } from '../api/api';
import { CartContext } from './CartContext';
import { UserContext } from './UserContext';

function Cart() {
    const { cart } = useContext(CartContext);
    const { user } = useContext(UserContext);

    const handlePlaceOrder = async () => {
        // Ensure each item has price_kc and quantity
        const items = cart.map(item => ({
            productId: item.id,
            price_kc: item.price_kc,
            quantity: item.quantity,
            totalPrice: item.price_kc * item.quantity
        }));

        // Calculate total prices
        const total_price_kc = items.reduce((total, item) => total + item.totalPrice, 0);
        const total_price_eu = total_price_kc * 0.04; // Assuming 1 Kč = 0.04 €

        const orderData = {
            user_id: user.id,
            order_date: new Date().toISOString(),
            status: 'processing',
            total_price_kc,
            total_price_eu,
            items
        };

        try {
            const response = await placeOrder(orderData);
            alert('Order placed successfully!');
        } catch (error) {
            console.error('Error placing order:', error);
            alert('Failed to place order!');
        }
    };

    const groupedCart = cart.reduce((acc, item) => {
        const existingItem = acc.find(cartItem => cartItem.id === item.id);
        if (existingItem) {
            existingItem.quantity += 1;
            existingItem.totalPrice = existingItem.quantity * item.price_kc;
        } else {
            acc.push({ ...item, quantity: 1, totalPrice: item.price_kc });
        }
        return acc;
    }, []);

    return (
        <div>
            <h2>Cart</h2>
            <ul>
                {groupedCart.map((item, index) => (
                    <li key={index}>
                        {item.name} - {item.quantity}x {item.totalPrice} Kč
                    </li>
                ))}
            </ul>
            <button onClick={handlePlaceOrder}>Place Order</button>
        </div>
    );
}

export default Cart;
